ArrayOfSqlQuery.SqlQuery:
DECLARE
	c        INTEGER := DBMS_SQL.open_cursor();
	errorpos integer := -1;
BEGIN
	BEGIN
		DBMS_SQL.parse(c, :sqltext, DBMS_SQL.native);
	EXCEPTION
		WHEN OTHERS THEN
			errorpos := DBMS_SQL.LAST_ERROR_POSITION();
	END;
	:errorpos := errorpos;
	DBMS_SQL.close_cursor(c);
END;
